// Get all items from the current execution
const items = $input.all();

// Group leads by account manager email
const leadsByManager = {};

for (let i = 0; i < items.length; i++) {
  const currentItem = items[i];
  const managerEmail = currentItem.json.Email;
  const managerName = currentItem.json['Account Manager'];

  // Get the corresponding lead data from the Follow-up node
  const leadData = $('Search Today's Follow-ups').all()[i].json;

  if (!leadsByManager[managerEmail]) {
    leadsByManager[managerEmail] = {
      email: managerEmail,
      managerName: managerName,
      leads: []
    };
  }

  leadsByManager[managerEmail].leads.push({
    name: leadData.Name || 'N/A',
    budget: leadData['Budget Range'] || 'N/A',
    lastContact: leadData['Last Contact'] || 'N/A',
    notes: leadData.Notes || 'N/A'
  });
}

// Convert to array for output
return Object.values(leadsByManager).map(manager => ({
  json: manager
}));

